section.screen:
#!/usr/bin/python
import sys
# ldif is a Python package of the python-ldap extension import ldif
LOGFILE="/var/log/newuser.log"
class MyParser(ldif.LDIFParser):
def handle(self, dn, entry): uid = entry["uid"][0] f = file(LOGFILE, "a") f.write("New user %s created\n" % uid) f.close()
parser = MyParser(file(sys.argv[1])) parser.parse()
