	  $o = new HtmlContainer;
	      $o->add(HtmlElement());
	      $o->push(HtmlContainer());
	          /* The HtmlElement are added to the latest added and open HtmlContainer */
	          $o->add(HtmlElement());
		  $o->push(HtmlContainer());
	              /* The HtmlElement are added to the latest added and open HtmlContainer */
		      $o->add(HtmlElement());
		      $o->add(HtmlElement());
		  /* closing the element list of the latest HtmlContainer */
                  $o->pop();
		  /* falling back to the previous HtmlContainer */
	          $o->add(HtmlElement());
              /* closing the element list of the latest HtmlContainer */
              $o->pop();
	      $o->add(HtmlElement());
	  /* Popping the root container */
	  $o->pop();
	  /* Display the HTML code */
	  $o->display();


	  class Table extends HtmlContainer {

              function Table() {
	          $this->HtmlContainer();
              }

	      function begin() {
	          return "<table>";
	      }

	      function end() {
	          return "</table>";
	      }

	  }

class Title Extends HtmlElement {

    function Title($text) {
        $this->$text = $text
    }

    function display() {
        print "<H1>" . $this->text . "</H1>";
    }

}

