  class NumericInputTpl extends InputTpl {

      function NumericInputTpl($name) {
          $this->name = $name;
	  $this->regexp = '/^[0-9]*$/';
      }

  }

  class MACInputTpl extends InputTpl {

      function MACInputTpl($name) {
          $this->name = $name;
	  $this->regexp = '/^([0-9a-f]{2}:){5}[0-9a-f]{2}$/i';
      }

  }

