function _samba_baseEdit($ldapArr,$postArr) {
  $checked = "checked"; //default value


  //fetch ldap updated info if we can
  if ($ldapArr["uid"][0]) {
      if  (hasSmbAttr($ldapArr["uid"][0]))   {
        $checked = "checked";
      }
      else {
        $checked = "";
      }
  } 

  //if we update a user but error when updating
  if ($postArr["buser"]) {
        if ($postArr["isSamba"]) {
          $checked = "checked";
        }
        else {
          $checked = "";
        }
    }

  print "<br/><h3> samba plugin</h3>\n";
  print '<table cellspacing="0">';

  $test = new TrFormElement("Accès samba",new CheckboxTpl("isSamba"));
  $test->setCssError("accesSmb");
  $param=array("value"=>$checked);
  $test->display($param);

}

  /**
   *  display input Element
   *  $arrParam accept ["value"] to corresponding value 
   */
  function display($arrParam) {

    $existACL=existAclAttr($this->template->name);

    //if not 
    if (!$existACL) {
      $aclattrright="rw";
      $isAclattrright=true;
    } else {
      $aclattrright=(getAclAttr($this->template->name));
      $isAclattrright=$aclattrright!='';
    }

    //if correct acl and exist acl
    if ($isAclattrright) {
      //if read only
      if ($aclattrright=="ro") {
        $this->template->displayRo($arrParam);
        //if all right
      } else if ($aclattrright=="rw") {
        $this->template->display($arrParam);
      }
      //if no right at all
    } else {
      $this->template->displayHide($arrParam);
    }

  }
}

